frappe.ui.form.on('Loan Details', {
    // --- existing triggers for EMI calc & duration ---
    onload:       function(frm) { calculate_loan_fields(frm); },
    refresh:      function(frm) { 
        calculate_loan_fields(frm);

        // ======== BUTTONS ========
        // Generate the EMI schedule
        frm.add_custom_button('Generate EMI Schedule', () => generate_schedule(frm));
        // Recalculate outstanding/status
        frm.add_custom_button('Recalculate EMI',     () => recalc_emi(frm));
    },
    validate:     function(frm) { calculate_loan_fields(frm); },

    loan_type:        function(frm) { calculate_loan_fields(frm); },
    loan_amount:      function(frm) { calculate_loan_fields(frm); },
    loan_duration:    function(frm) { calculate_loan_fields(frm); },
    interest_rate:    function(frm) { calculate_loan_fields(frm); },

    loan_start_date:  function(frm) { calculate_loan_duration(frm); },
    loan_end_date:    function(frm) { calculate_loan_duration(frm); },
});

// -------------------------
// BUTTON HANDLERS
// -------------------------

// 1) Build EMI Schedule using existing frm.doc.emi_amount, loan_duration & start_date
function generate_schedule(frm) {
    if (!frm.doc.emi_amount || !frm.doc.loan_duration || !frm.doc.loan_start_date) {
        frappe.msgprint('Please make sure EMI Amount, Loan Duration & Loan Start Date are set.');
        return;
    }

    // clear old schedule
    frm.clear_table('emi_schedule');

    // populate rows
    for (let i = 1; i <= frm.doc.loan_duration; i++) {
        let row = frm.add_child('emi_schedule');
        row.installment_no     = i;
        row.due_date           = frappe.datetime.add_months(frm.doc.loan_start_date, i - 1);
        row.emi_amount         = frm.doc.emi_amount;
        row.status             = 'Pending';
        row.outstanding_amount = frm.doc.emi_amount;
    }

    frm.refresh_field('emi_schedule');
}

// 2) Recalculate outstanding_amount & status for each row
function recalc_emi(frm) {
    if (!frm.doc.emi_schedule?.length) {
        frappe.msgprint('No EMI schedule found. Please Generate EMI Schedule first.');
        return;
    }

    frm.doc.emi_schedule.forEach(row => {
        let paid = parseFloat(row.amount_paid) || 0.0;
        let emi  = parseFloat(row.emi_amount)   || 0.0;
        let out  = frappe.utils.round(emi - paid, 2);

        frappe.model.set_value(row.doctype, row.name, 'outstanding_amount', out);
        frappe.model.set_value(row.doctype, row.name, 'status',
            out <= 0 ? 'Paid' : 'Pending'
        );
    });

    frm.refresh_field('emi_schedule');
}

// -------------------------
// YOUR EXISTING FUNCTIONS
// -------------------------

function calculate_loan_fields(frm) {
    var type = (frm.doc.loan_type || '').trim().toLowerCase();
    var P = frm.doc.loan_amount || 0;
    var n = frm.doc.loan_duration || 0;
    var r_annual = frm.doc.interest_rate || 0;

    if (!type || P <= 0 || n <= 0 || r_annual < 0) {
        frm.set_value('emi_amount', 0);
        frm.set_value('total_collection', 0);
        return;
    }

    var emi = 0.0;
    var total = 0.0;

    if (type === 'flat interest') {
        var total_interest = P * (r_annual / 100) * (n / 12);
        total = P + total_interest;
        emi = total / n;
    }
    else if (type === 'reducing rate interest' || type === 'reducin rate interest') {
        var r = (r_annual / 100) / 12;
        if (r === 0) {
            emi = P / n;
        } else {
            emi = (P * r * Math.pow(1 + r, n)) / (Math.pow(1 + r, n) - 1);
        }
        total = emi * n;
    }
    else {
        frappe.msgprint(__('Unknown Loan Type: {0}', [frm.doc.loan_type]));
        frm.set_value('emi_amount', 0);
        frm.set_value('total_collection', 0);
        return;
    }

    emi = flt(emi, 2);
    total = flt(total, 2);

    frm.set_value('emi_amount', emi);
    frm.set_value('total_collection', total);
}

function calculate_loan_duration(frm) {
    if (frm.doc.loan_start_date && frm.doc.loan_end_date) {
        var start = frappe.datetime.str_to_obj(frm.doc.loan_start_date);
        var end = frappe.datetime.str_to_obj(frm.doc.loan_end_date);

        if (start && end && end > start) {
            var months = (end.getFullYear() - start.getFullYear()) * 12
                       + (end.getMonth() - start.getMonth());
            frm.set_value('loan_duration', months);
            calculate_loan_fields(frm);
        } else {
            frm.set_value('loan_duration', 0);
            frm.set_value('emi_amount', 0);
            frm.set_value('total_collection', 0);
        }
    }
}

