frappe.ui.form.on('Payment Entry', {
    onload: function(frm) {
        // Auto-fill party from custom loan customer field
        if (frm.is_new() && frm.doc.party_type === "Customer" 
            && !frm.doc.party && frm.doc.custom_loan_customer) {
            frm.set_value("party", frm.doc.custom_loan_customer);
        }
    },
    
    setup: function(frm) {
        // Override the reference doctype query to include Loan Details
        frm.set_query("reference_doctype", "references", function () {
            let doctypes = ["Journal Entry"];
            if (frm.doc.party_type == "Customer") {
                doctypes = ["Sales Order", "Sales Invoice", "Journal Entry", "Dunning", "Loan Details"];
            } else if (frm.doc.party_type == "Supplier") {
                doctypes = ["Purchase Order", "Purchase Invoice", "Journal Entry"];
            }
            return {
                filters: { name: ["in", doctypes] },
            };
        });
    },
    
    after_save: update_emi,
    on_submit: update_emi
});

