frappe.ui.form.on("Payment Entry Reference", {
    reference_doctype: function (frm, cdt, cdn) {
        var row = locals[cdt][cdn];
        validate_reference_document_with_loan_details(frm, row);
    }
});

// Custom validation function that includes Loan Details
function validate_reference_document_with_loan_details(frm, row) {
    if (!row.reference_doctype) {
        return;
    }

    if (
        frm.doc.party_type == "Customer" &&
        !["Sales Order", "Sales Invoice", "Journal Entry", "Dunning", "Loan Details"].includes(row.reference_doctype)
    ) {
        frappe.model.set_value(row.doctype, row.name, "reference_doctype", null);
        frappe.msgprint(
            __(
                "Row #{0}: Reference Document Type must be one of Sales Order, Sales Invoice, Journal Entry, Dunning, or Loan Details",
                [row.idx]
            )
        );
        return false;
    }

    if (
        frm.doc.party_type == "Supplier" &&
        !["Purchase Order", "Purchase Invoice", "Journal Entry"].includes(row.reference_doctype)
    ) {
        frappe.model.set_value(row.doctype, row.name, "reference_doctype", null);
        frappe.msgprint(
            __(
                "Row #{0}: Reference Document Type must be one of Purchase Order, Purchase Invoice or Journal Entry",
                [row.idx]
            )
        );
        return false;
    }
}

