// Payment entry creation function
function make_payment_entry(frm, row) {
    const emi = flt(row.emi_amount);
    if (emi <= 0) {
        frappe.msgprint(__('Nothing to pay on this EMI.'));
        return;
    }

    if (!frm.doc.customer) {
        frappe.msgprint(__('Customer is required in Loan Details'));
        return;
    }

    frappe.model.with_doctype('Payment Entry', () => {
        let pe = frappe.model.get_new_doc('Payment Entry');

        // Set header fields
        frappe.model.set_value(pe.doctype, pe.name, {
            company: frm.doc.company,
            posting_date: row.due_date || frappe.datetime.get_today(),
            payment_type: 'Receive',
            party_type: 'Customer',
            party: frm.doc.customer,
            custom_loan_customer: frm.doc.customer,
            paid_from: 'Debtors - PD',
            paid_to: 'Cash - PD',
            paid_amount: emi,
            received_amount: emi,
            mode_of_payment: 'Cash',
            custom_payment_reference: row.name
        });

        // Add reference row using proper method
        let ref = frappe.model.add_child(pe, 'references');
        frappe.model.set_value(ref.doctype, ref.name, {
            reference_doctype: 'Loan Details',
            reference_name: frm.doc.name,
            total_amount: frm.doc.total_collection || emi,
            outstanding_amount: frm.doc.total_collection || emi,
            allocated_amount: emi
        });

        frappe.set_route('Form', 'Payment Entry', pe.name);
    });
}

// EMI status update function
function update_emi(frm) {
    if (!frm.doc.custom_payment_reference) return;
    
    frappe.call({
        method: 'frappe.client.set_value',
        args: {
            doctype: 'EMI Collection',
            name: frm.doc.custom_payment_reference,
            fieldname: {
                status: 'Paid',
                custom_payment_entry: frm.doc.name
            }
        },
        callback: function(r) {
            if (!r.exc) {
                frappe.show_alert({
                    message: 'EMI status updated to Paid',
                    indicator: 'green'
                });
            }
        }
    });
}

frappe.ui.form.on("Payment Entry", {
  setup: function(frm) {
    // ─── 1) Restrict the dropdown, including Loan Details ──────────────────────────
    frm.set_query("reference_doctype", "references", function() {
      let doctypes = ["Journal Entry"];
      if (frm.doc.party_type === "Customer") {
        doctypes = [
          "Sales Order",
          "Sales Invoice",
          "Journal Entry",
          "Dunning",
          "Loan Details"
        ];
      } else if (frm.doc.party_type === "Supplier") {
        doctypes = [
          "Purchase Order",
          "Purchase Invoice",
          "Journal Entry"
        ];
      }
      return { filters: { name: ["in", doctypes] } };
    });

    // ─── 2) Override the core validation to allow Loan Details ────────────────────
    frm.events.validate_reference_document = function(frm, row) {
      if (!row.reference_doctype) return;

      // for Customer
      if (frm.doc.party_type === "Customer") {
        const allowed = [
          "Sales Order",
          "Sales Invoice",
          "Journal Entry",
          "Dunning",
          "Loan Details"
        ];
        if (!allowed.includes(row.reference_doctype)) {
          frappe.model.set_value(row.doctype, row.name, "reference_doctype", null);
          frappe.msgprint(__(
            "Row #{0}: Reference Document Type must be one of " +
            allowed.join(", "),
            [row.idx]
          ));
          return false;
        }
      }

      // for Supplier
      if (frm.doc.party_type === "Supplier") {
        const allowed = [
          "Purchase Order",
          "Purchase Invoice",
          "Journal Entry"
        ];
        if (!allowed.includes(row.reference_doctype)) {
          frappe.model.set_value(row.doctype, row.name, "reference_doctype", null);
          frappe.msgprint(__(
            "Row #{0}: Reference Document Type must be one of " +
            allowed.join(", "),
            [row.idx]
          ));
          return false;
        }
      }
    };
  }
});

frappe.ui.form.on('Loan Details', {
refresh(frm) {
if (!frm.is_new()) {
frm.add_custom_button(__('Payment Entry'), () => {

const emi_rows = frm.doc.emi_schedule || [];

if (!emi_rows.length) {
frappe.msgprint('No EMIs found.');
return;
}


const options = emi_rows.map(row => ({
label: `Due: ${frappe.datetime.str_to_user(row.due_date)} | Amount: ${frappe.format(row.emi_amount, {fieldtype: 'Currency'})} | Status: ${row.status}`,
value: row.name
}));


let d = new frappe.ui.Dialog({
title: 'Select EMI to Pay',
fields: [
{
fieldtype: 'Select',
label: 'EMI',
fieldname: 'emi_row_name',
options: options,
reqd: 1
}
],
primary_action_label: 'Make Payment Entry',
primary_action(values) {
d.hide();

const emi_row = emi_rows.find(r => r.name === values.emi_row_name);

// 5. Create Payment Entry
frappe.new_doc('Payment Entry', {
payment_type: "Receive",
party_type: "Customer",
party: frm.doc.customer,
company: frm.doc.company,
posting_date: frappe.datetime.now_date(),
paid_amount: emi_row.emi_amount,
received_amount: emi_row.emi_amount,
remarks: `EMI Payment for Loan ${frm.doc.name}, Due ${emi_row.due_date}`,
references: [
{
reference_doctype: frm.doctype,
reference_name: frm.doc.name,
total_amount: emi_row.emi_amount,
allocated_amount: emi_row.emi_amount
}
],
payment_reference: emi_row.name
});
}
});

d.show();
}, __('Create'));
}
}
});

